mapper.select:
    select

      distinct


    from article_info t




      for update


      order by ${orderByClause}


		limit #{offset},#{limit}

select t1.id carId,t1.car carName,t1.icon carIcon,t1.price carPrice,t.uuid,t.title,t2.user_name userName,t2.head_img headImg
,t.icon,t.share_icon shareIcon,t.comments,t.praises,t.visitors,t.create_time createTime,t.category,t.title,t.content,t.source,t.media
from article_info t inner join brand_car t1 on (t.car_id=t1.id) left join user_info t2 on (t.user_id=t2.id) 


		and t1.brand_id=#{param.brandId}


		and t.state=#{param.state}


		and t.category=#{param.category}


		and t.title like concat('%',#{param.title},'%')


		and t.state in 

			#{state}



		and t.user_id=#{param.userId}




		order by #{param.orderBy}


		order by t.top_time desc,t.create_time desc



	limit #{param.offset},#{param.limit}
